Component.Content:
function BuscaCEP(primaryControl)
{
    // navigation data
    var globalContext = Xrm.Utility.getGlobalContext();

    // form context
    var formContext = primaryControl;
    var cep = formContext.getControl("acdym_cep").getAttribute().getValue();        

    console.log("acdym_cep: " + cep);

    if (cep)
    {
        cep = cep.replace(/\D/g, '');
        var parameters = {
            "CepInput": cep
        };

        var req = new XMLHttpRequest();
        req.open("POST", globalContext.getClientUrl() + "/api/data/v9.2/acdym_ActionCEP", true);
        req.setRequestHeader("Accept", "application/json");
        req.setRequestHeader("Content-Type", "application/json; charset=utf-8");
        req.setRequestHeader("OData-MaxVersion", "4.0");
        req.setRequestHeader("OData-Version", "4.0");
        req.onreadystatechange = function ()
        {            
            if (this.readyState == 4 /* complete */)
            {
                req.onreadystatechange = null;
                if (this.status == 200 || this.status == 204)
                {
                    Xrm.Utility.alertDialog("Action executed successfully...!");                                                    
                    var result = JSON.parse(this.response);                                                                                            
                    const jsonCEP = JSON.parse(result.ResultadoCEP);                    
                    console.log(jsonCEP);
                    formContext.getAttribute("acdym_logradouro").setValue(jsonCEP.logradouro);    
                    formContext.getAttribute("acdym_bairro").setValue(jsonCEP.bairro);    
                    formContext.getAttribute("acdym_localidade").setValue(jsonCEP.localidade);    
                    formContext.getAttribute("acdym_uf").setValue(jsonCEP.uf);    
                }
                else
                {
                    var error = JSON.parse(this.response).error;
                    Xrm.Utility.alertDialog("Error in Action: "+error.message);
                }
            }
        };
        req.send(JSON.stringify(parameters));
    }
}

function ValidarCNPJ(executionContext) {

    // validate that execution context was received
    if (executionContext === null)
        return;

    // get form context from execution context
    formContext = executionContext.getFormContext();

    // get the CNPJ value entered by the user
    var cnpj = executionContext.getEventSource().getValue();

    var label = executionContext.getEventSource().getName();

    // declare isValid as true and validate that the entered CNPJ is not fake,
    // otherwise set isValid to false
    var isValid = true;
    if (cnpj !== null & cnpj !== "") {
        cnpj = cnpj.replace(/[^\d]+/g, '');
        if (cnpj.length !== 14 ||
            cnpj === "00000000000000" ||
            cnpj === "11111111111111" ||
            cnpj === "22222222222222" ||
            cnpj === "33333333333333" ||
            cnpj === "44444444444444" ||
            cnpj === "55555555555555" ||
            cnpj === "66666666666666" ||
            cnpj === "77777777777777" ||
            cnpj === "88888888888888" ||
            cnpj === "99999999999999") {
            isValid = false;
        }

        var tamanho = cnpj.length - 2;
        var numeros = cnpj.substring(0, tamanho);
        digitos = cnpj.substring(tamanho);
        var soma = 0;
        var pos = tamanho - 7;

        for (i = tamanho; i >= 1; i--) {
            soma += numeros.charAt(tamanho - i) * pos--;
            if (pos < 2)
                pos = 9;
        }

        resultado = soma % 11 < 2 ? 0 : 11 - soma % 11;
        if (resultado.toString() !== digitos.charAt(0)) {
            isValid = false;
        }

        tamanho = tamanho + 1;
        numeros = cnpj.substring(0, tamanho);
        soma = 0;
        pos = tamanho - 7;
        for (i = tamanho; i >= 1; i--) {
            soma += numeros.charAt(tamanho - i) * pos--;
            if (pos < 2)
                pos = 9;
        }
        var resultado = soma % 11 < 2 ? 0 : 11 - soma % 11;
        if (resultado.toString() !== digitos.charAt(1)) {
            isValid = false;
        }
    }
    else {
        isValid = false;
    }

    if (isValid) {
        cnpj = cnpj.substring(0, 2) + '.' + cnpj.substring(2, 5) + '.' + cnpj.substring(5, 8) + '/' + cnpj.substring(8, 12) + '-' + cnpj.substring(12);
        cnpj = formContext.getAttribute(label).setValue(cnpj);
    } else {
        Xrm.Navigation.openAlertDialog({ confirmButtonLabel: "OK", text: "CNPJ " + cnpj + " Invalid" }, { height: 100, width: 300 }).then(
            function success(result) {
                cnpj = formContext.getAttribute(label).setValue("");
                formContext.getAttribute(campoSemFormatacao).setValue("");
            }
        );
    }
}

function ValidarCPF(executionContext) 
{
    // validate that execution context was received
    if (executionContext === null)
        return;

    var formContext = executionContext.getFormContext();
    var cpf = executionContext.getEventSource().getValue();
    var label = executionContext.getEventSource().getName();

    // assume valid until proven otherwise
    isValid = true;
    if (cpf !== null && cpf !== "") {
        cpf = cpf.replace(/[^\d]+/g, '');
        var Soma;
        var Resto;
        Soma = 0;
        if (cpf.length !== 11 ||
            cpf === "00000000000" ||
            cpf === "11111111111" ||
            cpf === "22222222222" ||
            cpf === "33333333333" ||
            cpf === "44444444444" ||
            cpf === "55555555555" ||
            cpf === "66666666666" ||
            cpf === "77777777777" ||
            cpf === "88888888888" ||
            cpf === "99999999999") {
            isValid = false;
        }

        for (i = 1; i <= 9; i++) Soma = Soma + parseInt(cpf.substring(i - 1, i)) * (11 - i);
        Resto = (Soma * 10) % 11;

        if ((Resto === 10) || (Resto === 11)) Resto = 0;
        if (Resto !== parseInt(cpf.substring(9, 10))) {
            isValid = false;
        }

        Soma = 0;
        for (i = 1; i <= 10; i++) Soma = Soma + parseInt(cpf.substring(i - 1, i)) * (12 - i);
        Resto = (Soma * 10) % 11;

        if ((Resto === 10) || (Resto === 11)) { Resto = 0; }
        if (Resto !== parseInt(cpf.substring(10, 11))) {
            isValid = false;
        }
    }
    else {
        isValid = false;
    }

    if (isValid) {
        cpf = cpf.substring(0, 3) + '.' + cpf.substring(3, 6) + '.' + cpf.substring(6, 9) + '-' + cpf.substring(9);
        cpf = formContext.getAttribute(label).setValue(cpf);
    } else {
        Xrm.Navigation.openAlertDialog({ confirmButtonLabel: "OK", text: "CPF: " + cpf + " - Invalid" }, { height: 100, width: 300 }).then(
            function success(result) {
                cpf = formContext.getAttribute(label).setValue("");
            }
        );
    }
}

